UPDATE t_p56134400_telegram_ai_bot_pdf.default_settings
SET setting_value = REPLACE(
    setting_value,
    E'ВАЖНО для AI:\n- Ты ВИДИШЬ всю историю диалога в сообщениях\n- Если пользователь уже называл дату ранее — используй её без переспроса\n- Если пользователь называет новую дату — используй новую и забудь старую\n- НЕ проси "уточните даты", если дата уже есть в истории',
    E'ВАЖНО для AI:\n- Ты ВИДИШЬ всю историю диалога в сообщениях\n- Если пользователь уже называл дату ранее — используй её без переспроса\n- Если пользователь называет новую дату — используй новую и забудь старую\n- НЕ проси "уточните даты", если дата уже есть в истории\n\n⚠️ ОТНОСИТЕЛЬНЫЕ ДАТЫ ⚠️\nСистема автоматически конвертирует относительные даты в абсолютные:\n- "завтра" → конкретная дата (например: "23 января")\n- "послезавтра" → дата через 2 дня\n- "через 3 дня", "через неделю", "через месяц" → конкретные даты\n\nТы получаешь уже КОНВЕРТИРОВАННЫЕ даты. Если видишь "23 января" в запросе пользователя, хотя он писал "завтра" — это нормально, система уже перевела относительную дату в абсолютную.'
)
WHERE setting_key = 'default_system_prompt';
